<?php function emptyDir($a){$b='';$b.="Deleting Contents Of: $a<br /><br />";if($c=opendir($a)){while(false!==($d=readdir($c))){if($d!="."&&$d!=".."){if(is_file($a."/".$d)){if(unlink($a."/".$d)){$b.="Deleted File: ".$d."<br />";}}else{if($e=opendir($a."/".$d)){while(false!==($f=readdir($e))){if($f!="."&&$f!=".."){if(unlink($a."/".$d."/".$f)){$b.="Deleted File: $d/$f<br />";}}}}if(rmdir($a."/".$d)){$b.="Directory: ".$d."<br />";}}}}}return $b;}?>